"use client";

import { useEffect } from "react";
import { TOKEN } from "@/constants/common/constant";
import { Storage } from "@/apis/storage/storage";

export default function TokenInitializer() {
  useEffect(() => {
    const getCookie = (name: string) => {
      if (typeof document === "undefined") return null;
      const value = `; ${document.cookie}`;
      const parts = value.split(`; ${name}=`);
      if (parts.length === 2) return parts.pop()?.split(";").shift();
      return null;
    };

    const clearCookie = (name: string) => {
      if (typeof document === "undefined") return;
      document.cookie = `${name}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`;
    };

    try {
      const accessToken = getCookie("accessToken");
      const refreshToken = getCookie("refreshToken");

      console.log("Token Initializer - Found Access Token:", !!accessToken);
      console.log("Token Initializer - Found Refresh Token:", !!refreshToken);

      if (accessToken) {
        console.log("Saving access token to localStorage");
        Storage.setItem(TOKEN.ACCESS, accessToken);
        clearCookie("accessToken");
      }

      if (refreshToken) {
        console.log("Saving refresh token to localStorage");
        Storage.setItem(TOKEN.REFRESH, refreshToken);
        clearCookie("refreshToken");
      }

      if (accessToken || refreshToken) {
        console.log("Tokens saved successfully");
        console.log("Access Token in storage:", Storage.getItem(TOKEN.ACCESS) ? "✓" : "✗");
        console.log("Refresh Token in storage:", Storage.getItem(TOKEN.REFRESH) ? "✓" : "✗");
      }
    } catch (error) {
      console.error("Error in TokenInitializer:", error);
    }
  }, []);

  return null;
}

