  font-family: Sacheon_Hanggong_OTF, sans-serif;
  font-size: 12px;
  font-weight: 400;
  color: ${color.black};
  text-align: center;
  line-height: 1;
  margin: 0;
`;

const ErrorMessage = styled.div`
  padding: 12px 16px;
  background-color: #fee;
  border: 1px solid #fcc;
  border-radius: 8px;
  color: #c33;
  font-size: 14px;
  text-align: center;
  margin-bottom: 20px;
  max-width: 300px;
`;

const GoogleLoginButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 16px;
  width: 165px;
  height: 36px;
  border: 1px solid ${color.gray300};
  border-radius: 16px;
  background-color: ${color.white};
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background-color: ${color.gray50};
  }

  &:active {
    opacity: 0.8;
  }
`;

const GoogleIconWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 20px;
  height: 20px;
`;

