  color: ${color.gray600};
  padding: 40px 20px;
  margin: 0;
`;

"use client";

import React from "react";
import { useRouter, useSearchParams } from "next/navigation";
import styled from "@emotion/styled";
import Header from "@/components/common/header";
import color from "@/packages/design-system/src/color";
import { CheckComplete } from "@/../public/svg/svg";
import { reportComment } from "@/services/board/api";

type ReportReason =
  | "유해한 내용을 포함하고 있어요"
  | "명예훼손 또는 저작권이 침해되었어요"
  | "욕설/생명경시/혐오 표현이 사용되었어요"
  | "질문이 아니에요"
  | "기타";

const reportReasons: ReportReason[] = [
  "유해한 내용을 포함하고 있어요",
  "명예훼손 또는 저작권이 침해되었어요",
  "욕설/생명경시/혐오 표현이 사용되었어요",
  "질문이 아니에요",
  "기타",
];

const CommentReportPage = () => {
  const router = useRouter();
  const searchParams = useSearchParams();
  const commentId = searchParams?.get("commentId") as string;

  const [selectedReason, setSelectedReason] =
    React.useState<ReportReason | null>(null);
  const [detailText, setDetailText] = React.useState("");
  const [showCancelModal, setShowCancelModal] = React.useState(false);
  const [showCompleteModal, setShowCompleteModal] = React.useState(false);
  const [isSubmitting, setIsSubmitting] = React.useState(false);

  const handleSubmit = async () => {
    if (!selectedReason || !detailText.trim() || !commentId) return;

    try {
      setIsSubmitting(true);
      await reportComment(commentId, selectedReason, detailText);
      setShowCompleteModal(true);
    } catch (error) {
      void error;
      alert("신고 접수에 실패했습니다. 다시 시도해주세요.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCompleteClose = () => {
    setShowCompleteModal(false);
    router.back();
  };

  const handleCloseClick = () => {
    setShowCancelModal(true);
  };

  const handleConfirmCancel = () => {
    router.back();
  };

  const handleCancelModal = () => {
    setShowCancelModal(false);
  };

  return (
    <StyledPage>
      <Header types="close" text="신고하기" onClose={handleCloseClick} />
      <Container>
        <Section>
          <SubTitle>신고할 내용</SubTitle>
          <Title>게시판 댓글 신고하기</Title>
        </Section>

        <ReasonSection>
          <LabelWithRequired>
            <Label>신고 사유 선택</Label>
            <Required>*</Required>
          </LabelWithRequired>

          <ReasonList>
            {reportReasons.map((reason) => (
              <ReasonButton
                key={reason}
                isSelected={selectedReason === reason}
                onClick={() => setSelectedReason(reason)}
              >
                {reason}
              </ReasonButton>
            ))}
          </ReasonList>
        </ReasonSection>

        <DetailSection>
          <LabelWithRequired>
            <Label>상세 내용</Label>
            <Required>*</Required>
          </LabelWithRequired>

          <DetailTextarea
            placeholder="신고 사유를 더 자세히 작성해주세요"
            value={detailText}
            onChange={(e) => setDetailText(e.target.value.slice(0, 500))}
          />
          <CharCount>{detailText.length}/500</CharCount>
        </DetailSection>

        <SubmitButton
          onClick={handleSubmit}
          disabled={!selectedReason || !detailText.trim() || isSubmitting}
        >
          {isSubmitting ? "신고 중..." : "신고 접수하기"}
        </SubmitButton>
      </Container>

      {showCancelModal && (
        <>
          <ModalOverlay onClick={handleCancelModal} />
          <ModalContainer>
            <ModalContent>
              <ModalTextContainer>
                <ModalTitle>
                  신고 작성을 <CancelText>취소</CancelText>하시겠어요?
                </ModalTitle>
                <ModalSubtitle>
                  이전으로 돌아가며 작성 내용이 저장되지 않습니다.
                </ModalSubtitle>
              </ModalTextContainer>
              <ModalButtonContainer>
                <ModalCancelButton onClick={handleCancelModal}>
                  계속 작성
                </ModalCancelButton>
                <ModalConfirmButton onClick={handleConfirmCancel}>
                  나가기
                </ModalConfirmButton>
              </ModalButtonContainer>
            </ModalContent>
          </ModalContainer>
        </>
      )}

      {showCompleteModal && (
        <>
          <ModalOverlay onClick={handleCompleteClose} />
          <ModalContainer>
            <CompleteModalContent>
              <CheckComplete />
              <CompleteModalTextContainer>
                <CompleteModalTitle>
                  신고가 <SuccessText>접수</SuccessText>되었습니다!
                </CompleteModalTitle>
                <CompleteModalSubtitle>
                  <p>신고 내용을 검토하여</p>
                  <p>빠른 시일 내 조치하겠습니다.</p>
                </CompleteModalSubtitle>
              </CompleteModalTextContainer>
              <CompleteModalButton onClick={handleCompleteClose}>
                확인
              </CompleteModalButton>
            </CompleteModalContent>
          </ModalContainer>
        </>
      )}
    </StyledPage>
  );
};

`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  gap: 40px;
  width: 100%;
  flex: 1;
  padding: 20px 0;
`;

const Section = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  padding: 0 20px;
`;

const SubTitle = styled.p`
  font-family: Pretendard, sans-serif;
  font-size: 14px;
  font-weight: 400;
  color: ${color.gray700};
  line-height: 1;
  margin: 0;
`;

const Title = styled.h1`
  font-family: Pretendard, sans-serif;
  font-size: 22px;
  font-weight: 700;
  color: ${color.black};
  line-height: 1;
  margin: 0;
`;

const ReasonSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  padding: 0 20px;
`;

const LabelWithRequired = styled.div`
  display: flex;
  gap: 4px;
  align-items: center;
`;

const Label = styled.p`
  font-family: Pretendard, sans-serif;
  font-size: 15px;
  font-weight: 600;
  color: ${color.gray700};
  line-height: 1;
  margin: 0;
`;

const Required = styled.span`
  font-family: Pretendard, sans-serif;
  font-size: 15px;
  font-weight: 600;
  color: #e71d36;
  line-height: 1;
  margin: 0;
`;

const ReasonList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  padding: 0 20px;
`;

const ReasonButton = styled.button<{ isSelected: boolean }>`
  border: 1px solid
    ${(props) => (props.isSelected ? color.primary : color.gray100)};
  border-radius: 16px;
  padding: 19px 20px;
  background-color: ${(props) =>
    props.isSelected ? color.primary : color.white};
  font-family: Pretendard, sans-serif;
  font-size: 15px;
  font-weight: 600;
  color: ${(props) => (props.isSelected ? color.white : color.gray600)};
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    opacity: 0.8;
  }
`;

const DetailSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  padding: 0 20px;
`;

const DetailTextarea = styled.textarea`
  border: 1px solid ${color.gray100};
  border-radius: 16px;
  padding: 20px;
  min-height: 120px;
  font-family: Pretendard, sans-serif;
  font-size: 12px;
  font-weight: 400;
  color: ${color.black};
  background-color: ${color.white};
  resize: none;
  outline: none;

  &::placeholder {
    color: ${color.gray300};
  }
`;

const CharCount = styled.p`
  font-family: Pretendard, sans-serif;
  font-size: 13px;
  font-weight: 400;
  color: ${color.gray300};
  line-height: 1;
  margin: 0;
  padding: 0 20px;
`;

const SubmitButton = styled.button`
  background-color: ${(props) =>
    props.disabled ? color.gray200 : color.primary};
  border: none;
  border-radius: 100px;
  padding: 16px 20px;
  margin: 0 20px;
