  votes: number;
  percentage: number;
}

import { VoteClosureType } from "./api";

export interface VotePayload {
  id: string;
  title: string;
  category: string;
  status: string;
  totalResponses: number;
  finishedAt: number;
  options: OptionWithStats[];
  hasVoted: boolean;
  closureType?: VoteClosureType;
}

export interface MyVote {
  id: string;
  title: string;
  category: string;
  status: string;
  totalResponses: number;
  finishedAt: string | null;
  options: OptionWithStats[];
  hasVoted: boolean;
}

export interface GetMyVotesResponse {
  data: MyVote[];
}

